import React, { useState, useEffect } from "react";
import MovieList from './Movie';

const App = () => {
  const [movies, setMovies] = useState([]);
  const getMovieRequest = async () => {
		const url = `http://www.omdbapi.com/?s=comedy&apikey=ce762116`;

		const response = await fetch(url);
		const responseJson = await response.json();

		if (responseJson.Search) {
			setMovies(responseJson.Search);
		}
	};

  useEffect(() => {
		getMovieRequest();
	}, []);

  return (
    <div className="container-fluid movie-app">
      <div style={{overflowx: 'auto', marginBottom: '20px', display: 'flex', flexDirection: 'row', justifyContent: 'space-evenly', alignItems: 'center', flexWrap: 'wrap'}} className="row">
        <MovieList movies={movies} />
      </div>
    </div>
  );
};

export default App;
