import React, { useEffect, useState } from 'react';
import { 
    Layout, 
    Input, 
    Row, 
    Col, 
    Card, 
    Tag, 
    Spin, 
    Alert, 
    Modal, 
    Typography 
} from 'antd';
import 'antd/dist/antd.css';

const API_KEY = 'ce762116';
const { Content } = Layout;
const { Search } = Input;
const { Meta } = Card;
const TextTitle = Typography.Title;

const SearchBox = ({searchHandler}) => {
  return (
      <Row>
          <Col span={8} offset={6}>
              <Search
                  placeholder="Enter Comedy Movies, Series..."
                  enterButton="Search"
                  size="large"
                  onSearch={value => searchHandler(value)}
              />
          </Col>
      </Row>
  )
}



const ColCardBox = ({Title, imdbID, Poster, Type, ShowDetail, DetailRequest, ActivateModal}) => {

  return (
      <Col style={{margin: '20px 0'}} className="gutter-row" span={4}>
          <div className="gutter-box">
              <Card
                  style={{ width: 200 }}
                  cover={
                      <img
                        alt={Title}
                        src={Poster === 'N/A' ? 'https://placehold.it/198x264&text=Image+Not+Found' : Poster}
                />
                  }
              >
                  <Meta
                          title={Title}
                          description={false}
                  />
                  <Row style={{marginTop: '10px'}} className="gutter-row">
                      <Col>
                          <Tag color="magenta">Movie / Series</Tag>
                      </Col>
                  </Row>
              </Card>
          </div>
      </Col>
  )
}

const MovieDetail = ({Title, Poster, imdbRating, Rated, Runtime, Genre, Plot}) => {
    return (
        <Row>
            <Col span={11}>
                <img 
                    src={Poster === 'N/A' ? 'https://placehold.it/198x264&text=Image+Not+Found' : Poster} 
                    alt={Title} 
                />
            </Col>
            <Col span={13}>
                <Row>
                    <Col span={21}>
                        <TextTitle level={4}>{Title}</TextTitle></Col>
                    <Col span={3} style={{textAlign:'right'}}>
                        <TextTitle level={4}><span style={{color: '#41A8F8'}}>{imdbRating}</span></TextTitle>
                    </Col>
                </Row>
                <Row style={{marginBottom: '20px'}}>
                    <Col>
                        <Tag>{Rated}</Tag> 
                        <Tag>{Runtime}</Tag> 
                        <Tag>{Genre}</Tag>
                    </Col>
                </Row>
                <Row>
                    <Col>{Plot}</Col>
                </Row>
            </Col>
        </Row>
    )
}

function App() {

  const [data, setData] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [q, setQuery] = useState('Brooklyn Nine-Nine');
  const [activateModal, setActivateModal] = useState(false);
  const [detail, setShowDetail] = useState(false);
  const [detailRequest, setDetailRequest] = useState(false);

  useEffect(() => {

    setLoading(true);
    setError(null);
    setData(null);

    fetch(`http://www.omdbapi.com/?s=${q}&apikey=${API_KEY}`)
    .then(resp => resp)
    .then(resp => resp.json())
    .then(response => {
        if (response.Response === 'False') {
            setError(response.Error);
        }
        else {
            setData(response.Search);
        }

        setLoading(false);
    })
    .catch(({message}) => {
        setError(message);
        setLoading(false);
    })
}, [q]);


  return (
      <div className="App">
          <Layout className="layout">
              <Content style={{ padding: '0 50px' }}>
                  <div style={{ background: '#fff', padding: 24, minHeight: 280 }}>
                    <SearchBox searchHandler={setQuery} />
                      <br />
                      <Row gutter={16} type="flex" justify="center">
                          <ColCardBox />
                      </Row>

                  </div>
              </Content>
          </Layout>
      </div>
  );
}

export default App;
