import React from 'react';
import ReactDOM from 'react-dom';


class  FetchUser extends React.Component{

  state=
  {
    loding:true,
    people:[],
  };

async componentDidMount(){
  const url=`http://www.omdbapi.com/?s=comedy&apikey=a80b9720`;
  const response=await fetch(url);
  const data=await response.json();
  //this.setState({person:JSON.stringify(data)})
  this.setState({people:data.results,loding:false});

}

render()
{
return (

<div style={{color:'blue'}}>
  {this.state.people.map(person=>(
    <div key={person.login.uuid}>
    <img src={person.Poster} alt={person.Title}></img>
   <div>Title:{person.Title}</div>
   <div>first:{person.name.first}</div>
   <div>last:{person.name.last}</div>
   <div>phone:{person.phone}</div>
   <div>email:{person.email}</div>
   </div>
  ))}




</div>
);
}

}

export default FetchUser;
