// import React from 'react';
// import '../../App.css';

// export default function ScienceFiction() {
//   return <h1 className='scienceandfiction'>Science and Fiction</h1>;
// }


import React from 'react';
import ReactDOM from 'react-dom';


class  FetchUser extends React.Component{

  state=
  {
    loding:true,
    people:[],
  };

async componentDidMount(Title, imdbID, Poster, Type, ShowDetail, DetailRequest, ActivateModal){
  const API_KEY = 'ce762116';
  const url=`http://www.omdbapi.com/?i=${imdbID}&apikey=${API_KEY}`;
  const response=await fetch(url);
  const data=await response.json();
  //this.setState({person:JSON.stringify(data)})
  this.setState({people:data.results,loding:false});

}

render()
{
if(this.state.loding)
{
return <div>loding...</div>
}
if(!this.state.people.length)
{
return <div>did not get person!</div>
}
return (

<div style={{color:'blue'}}>
  {this.state.people.map(person=>(
    <div key={person.login.uuid}>
    <img src={person.Poster.large} alt={person.Title}></img>
   <div>Title:{person.Title}</div>
   <div>first:{person.name.first}</div>
   <div>last:{person.name.last}</div>
   <div>phone:{person.phone}</div>
   <div>email:{person.email}</div>
   </div>
  ))}




</div>
);
}

}

ReactDOM.render(

  <FetchUser />,

document.getElementById('root')
);
