import React from "react";
import styled from "styled-components";


const MovieContainer = styled.div`
  display: flex;
  flex-direction: column;
  padding: 10px;
  width: 280px;
  box-shadow: 0 3px 10px 0 #aaa;
  cursor: pointer;
  position: relative;
`;
const CoverImage = styled.img`
  object-fit: cover;
  position: relative;
`;
const MovieName = styled.span`
  font-size: 18px;
  font-weight: 600;
  color: rgb(65, 168, 248);
  margin: 15px 0 3rem 0;
  white-space: none;
  overflow: hidden;
  text-align: center;
  position: relative;
`;
const InfoColumn = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  position: absolute;
  bottom: 5px;
`;
const MovieInfo = styled.span`
  font-size: 13px;
  font-weight: 500;
  color: #c41d7f;
  white-space: nowrap;
  overflow: hidden;
  text-transform: capitalize;
  text-align: ellipsis;
`;
const MovieComponent = (props) => {
  const { Title, Year, Type, Poster, Href } = props.movie;

  return (
      <MovieContainer>
          <CoverImage src={Poster} alt={Title} />
          <MovieName>{Title}</MovieName>
          <InfoColumn>
              <MovieInfo>{Year}</MovieInfo>
              <MovieInfo>{Type}</MovieInfo>
          </InfoColumn>
      </MovieContainer>
  );
};
export default MovieComponent;
