import React from "react";
import styled from "styled-components";
import {Link} from 'react-router-dom';
import { 
  Layout, 
  Input, 
  Row, 
  Col, 
  Card, 
  Tag, 
  Spin, 
  Alert, 
  Modal, 
  Typography 
} from 'antd';
import 'antd/dist/antd.css';

const NewsContainer = styled.div`
  display: flex;
  flex-direction: column;
  padding: 10px;
  width: 280px;
  box-shadow: 0 3px 10px 0 #aaa;
  cursor: pointer;
  position: relative;
`;
const CoverImage = styled.img`
  object-fit: cover;
  position: relative;
`;
const NewName = styled.span`
  font-size: 18px;
  font-weight: 600;
  color: rgb(65, 168, 248);
  margin: 15px 0 3rem 0;
  white-space: none;
  overflow: hidden;
  text-align: center;
  position: relative;
`;
const InfoColumn = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  position: absolute;
  bottom: 5px;
`;
const MovieInfo = styled.span`
  font-size: 13px;
  font-weight: 500;
  color: #c41d7f;
  white-space: nowrap;
  overflow: hidden;
  text-transform: capitalize;
  text-align: ellipsis;
`;
const MovieComponent = (props) => {
  const { Title, Date, Writer, Poster, Href } = props.movie;

  return (
    <Link target="_blank" to = {{ pathname: Href}}>
      <NewsContainer>
            <CoverImage src={Poster} alt={Title} />
            <NewName>{Title}</NewName>
            <Row style={{marginBottom: '10px'}}>
            <Col>
                <Tag>{Date}</Tag>
                <Tag style={{backgroundColor: '#c41d7f'}}>{Writer}</Tag>
            </Col>
            </Row>
      </NewsContainer>
    </Link>
  );
};
export default MovieComponent;
