import React, { useEffect, useState } from 'react';
import { 
    Layout, 
    Input, 
    Row, 
    Col, 
    Card, 
    Tag, 
    Spin, 
    Alert, 
    Modal, 
    Typography 
} from 'antd';
import 'antd/dist/antd.css';

const API_KEY = 'ce762116';
const { Content } = Layout;
const { Search } = Input;
const { Meta } = Card;
const TextTitle = Typography.Title;

const SearchBox = ({searchHandler}) => {
  return (
      <Row>
          <Col span={8} offset={6}>
              <Search
                  placeholder="Enter Comedy Movies, Series..."
                  enterButton="Search"
                  size="large"
                  onSearch={value => searchHandler(value)}
              />
          </Col>
      </Row>
  )
}

const ColCardBox = () => {

  return (
      <Col style={{margin: '20px 0'}} className="gutter-row" span={4}>
          <div className="gutter-box">
              <Card
                  style={{ width: 200 }}
                  cover={
                      <img
                          alt=""
                          src="https://placehold.it/198x264&text=Image+Not+Found"
                      />
                  }
              >
                  <Meta
                          title="Title Goes Here"
                          description={false}
                  />
                  <Row style={{marginTop: '10px'}} className="gutter-row">
                      <Col>
                          <Tag color="magenta">Movie / Series</Tag>
                      </Col>
                  </Row>
              </Card>
          </div>
      </Col>
  )
}

function App() {

  const [data, setData] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [q, setQuery] = useState('batman');


  return (
      <div className="App">
          <Layout className="layout">
              <Content style={{ padding: '0 50px' }}>
                  <div style={{ background: '#fff', padding: 24, minHeight: 280 }}>

                      <SearchBox />
                      <br />
                      <Row gutter={16} type="flex" justify="center">
                          <ColCardBox />
                      </Row>

                  </div>
              </Content>
          </Layout>
      </div>
  );
}

export default App;
