import React from 'react';

const API_KEY = 'ce762116';


const ColCardBox = ({Title, imdbID, Poster, Type, ShowDetail, DetailRequest, ActivateModal}) => {

  const clickHandler = () => {

      // Display Modal and Loading Icon
      ActivateModal(true);
      DetailRequest(true);

      fetch(`http://www.omdbapi.com/?i=${imdbID}&apikey=${API_KEY}`)
      .then(resp => resp)
      .then(resp => resp.json())
      .then(response => {
          DetailRequest(false);
          ShowDetail(response);
      })
      .catch(({message}) => {
          DetailRequest(false);
      })
  }
}

function ComedyCategory() {

  const students = [  
    {  
      'id': 1,   
      'name': 'Jack',   
      'email': 'jack@gmail.com'  
    },  
    {  
      'id': 2,   
      'name': 'Mary',   
      'email': 'mary@gmail.com'  
    },  
    {  
      'id': 3,   
      'name': 'John',   
      'email': 'john@gmail.com'  
    },  
];

return (
  <div className="container">       
        <table className="table table-bordered">  
            <tr>  
                <th>ID</th>  
                <th>Name</th>  
                <th>Email</th>  
            </tr>  

            {students.map((student, index) => (  
              <tr data-index={index}>  
                <td>{student.id}</td>  
                <td>{student.name}</td>  
                <td>{student.email}</td>  
              </tr>  
            ))}  

        </table>  

    </div>  
  );
}

export default ComedyCategory;
