import React, { useState, useEffect } from "react";
import MovieList from './Movie';
import { 
  Layout, 
  Input, 
  Row, 
  Col, 
  Card, 
  Tag, 
  Spin, 
  Alert, 
  Modal, 
  Typography 
} from 'antd';
import 'antd/dist/antd.css';

const API_KEY = 'a80b9720';
const { Content } = Layout;
const { Search } = Input;
const { Meta } = Card;
const TextTitle = Typography.Title;

const MovieDetail = ({Title, Poster, imdbRating, Rated, Runtime, Genre, Plot}) => {
  return (
      <Row>
          <Col span={11}>
              <img 
                  src={Poster === 'N/A' ? 'https://placehold.it/198x264&text=Image+Not+Found' : Poster} 
                  alt={Title} 
              />
          </Col>
          <Col span={13}>
              <Row>
                  <Col span={21}>
                      <TextTitle level={4}>{Title}</TextTitle></Col>
                  <Col span={3} style={{textAlign:'right'}}>
                      <TextTitle level={4}><span style={{color: '#41A8F8'}}>{imdbRating}</span></TextTitle>
                  </Col>
              </Row>
              <Row style={{marginBottom: '20px'}}>
                  <Col>
                      <Tag>{Rated}</Tag> 
                      <Tag>{Runtime}</Tag> 
                      <Tag>{Genre}</Tag>
                  </Col>
              </Row>
              <Row>
                  <Col>{Plot}</Col>
              </Row>
          </Col>
      </Row>
  )
}

const App = () => {
  const [movies, setMovies] = useState([]);
  const getMovieRequest = async () => {
		const url = `http://www.omdbapi.com/?s=love&apikey=ce762116`;

		const response = await fetch(url);
		const responseJson = await response.json();

		if (responseJson.Search) {
			setMovies(responseJson.Search);
		}
	};

  useEffect(() => {
		getMovieRequest();
	}, []);

  return (
    <div className="container-fluid movie-app">
      <div style={{overflowx: 'auto', marginTop: '5rem', display: 'flex', flexDirection: 'row', justifyContent: 'space-evenly', alignItems: 'center', flexWrap: 'wrap'}} className="row">
        <MovieList movies={movies} style={{marginBottom: '2rem'}} />

      </div>
    </div>
  );
};

export default App;
