import React, { useState } from "react";
import styled from "styled-components";
import MovieComponent from "./MovieInfo/MovieComponent";
import MovieBuyTicket from "./MovieInfo/MovieBuyTicket";

const Container = styled.div`
  display: flex;
  flex-direction: column;
`;
const MovieListContainer = styled.div`
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  padding: 30px;
  gap: 25px;
  justify-content: space-evenly;;
`;
const Placeholder = styled.img`
  width: 120px;
  height: 120px;
  margin: 150px;
  opacity: 50%;
`;

function App() {
  const [movieList] = useState([
    {
      "Title": "No Time to Die",
      "Year": "2019",
      "imdbID": "tt10839440",
      "Type": "movie",
      "Poster": "https://media.cinemaximum.com.tr/255//Files/POSTER/bond_yeni.jpg",
      "Href": "https://www.cinemaximum.com.tr/olmek-icin-zaman-yok-filmi"
    },
    {
      "Title": "The Boss Baby: Family Business",
      "Year": "2021",
      "imdbID": "tt6932874",
      "Type": "movie",
      "Poster": "https://m.media-amazon.com/images/M/MV5BYWY1N2I5MGUtZTBmNS00MWZmLWEyNzAtZjliNzk0ZDVkYzI1XkEyXkFqcGdeQXVyMTEyMjM2NDc2._V1_SX300.jpg",
      "Href": "https://www.cinemaximum.com.tr/patron-bebek-2-aile-sirketi-filmi"
    },
    {
      "Title": "Luca",
      "Year": "2021",
      "imdbID": "tt12801262",
      "Type": "movie",
      "Poster": "https://m.media-amazon.com/images/M/MV5BZTQyNTU0MDktYTFkYi00ZjNhLWE2ODctMzBkM2U1ZTk3YTMzXkEyXkFqcGdeQXVyNTI4MzE4MDU@._V1_SX300.jpg",
      "Href": "https://www.cinemaximum.com.tr/luka-filmi"
    },
    {
      "Title": "Interstellar",
      "Year": "2014",
      "imdbID": "tt0816692",
      "Type": "movie",
      "Poster": "https://m.media-amazon.com/images/M/MV5BZjdkOTU3MDktN2IxOS00OGEyLWFmMjktY2FiMmZkNWIyODZiXkEyXkFqcGdeQXVyMTMxODk2OTU@._V1_SX300.jpg"
    },
    {
      "Title": "Logan",
      "Year": "2017",
      "imdbID": "tt3315342",
      "Type": "movie",
      "Poster": "https://m.media-amazon.com/images/M/MV5BYzc5MTU4N2EtYTkyMi00NjdhLTg3NWEtMTY4OTEyMzJhZTAzXkEyXkFqcGdeQXVyNjc1NTYyMjg@._V1_SX300.jpg"
    },
    {
      "Title": "Avatar",
      "Year": "2009",
      "imdbID": "tt0499549",
      "Type": "movie",
      "Poster": "https://m.media-amazon.com/images/M/MV5BMTYwOTEwNjAzMl5BMl5BanBnXkFtZTcwODc5MTUwMw@@._V1_SX300.jpg"
    },
    {
      "Title": "Transformers",
      "Year": "2007",
      "imdbID": "tt0418279",
      "Type": "movie",
      "Poster": "https://m.media-amazon.com/images/M/MV5BNDg1NTU2OWEtM2UzYi00ZWRmLWEwMTktZWNjYWQ1NWM1OThjXkEyXkFqcGdeQXVyMTQxNzMzNDI@._V1_SX300.jpg"
    },
    {
      "Title": "Terminator 2: Judgment Day",
      "Year": "1991",
      "imdbID": "tt0103064",
      "Type": "movie",
      "Poster": "https://m.media-amazon.com/images/M/MV5BMGU2NzRmZjUtOGUxYS00ZjdjLWEwZWItY2NlM2JhNjkxNTFmXkEyXkFqcGdeQXVyNjU0OTQ0OTY@._V1_SX300.jpg"
    },
  ]);
  const [selectedMovie, onMovieSelect] = useState();
  return (
    <Container>
      {selectedMovie && <MovieBuyTicket selectedMovie={selectedMovie} onMovieSelect={onMovieSelect}/>}
      <MovieListContainer>
        {movieList?.length ? (
          movieList.map((movie, index) => (
            <MovieComponent
              key={index}
              movie={movie}
              onMovieSelect={onMovieSelect}
            />
          ))
        ) : (
          <Placeholder src=""/>
        )}
      </MovieListContainer>
    </Container>
  );
}

export default App;
