import React from "react";
import styled from "styled-components";
import {Link} from 'react-router-dom';
import { 
  Layout, 
  Input, 
  Row, 
  Col, 
  Card, 
  Tag, 
  Spin, 
  Alert, 
  Modal, 
  Typography 
} from 'antd';
import 'antd/dist/antd.css';

const NewsContainer = styled.div`
  display: flex;
  flex-direction: column;
  padding: 10px;
  width: 280px;
  box-shadow: 0 3px 10px 0 #aaa;
  cursor: pointer;
  position: relative;
`;
const CoverImage = styled.img`
  object-fit: cover;
  position: relative;
`;
const NewName = styled.span`
  font-size: 18px;
  font-weight: 600;
  color: rgb(65, 168, 248);
  margin: 15px 0 3rem 0;
  white-space: none;
  overflow: hidden;
  text-align: center;
  position: relative;
`;
const MovieComponent = (props) => {
  const { Title, Date, Writer, Poster, Href } = props.movie;

  return (
    <Link target="_blank" to = {{ pathname: Href}}>
      <NewsContainer>
            <CoverImage src={Poster} alt={Title} />
            <NewName style={{fontWeight: '500', fontSize: '14px'}}>{Title}</NewName>
            <Row style={{marginBottom: '10px', display: 'flex', flexDirection: 'column'}}>
              <Col>
                  <Tag style={{color: '#c41d7f', fontSize: '11px'}}>{Date}</Tag>
              </Col>
              <Col>
                  <Tag style={{color: 'rgb(65, 168, 248)', fontSize: '13px', backgroundColor: '#c41d7f', marginTop: '5px'}}>{Writer}</Tag>
              </Col>
            </Row>
      </NewsContainer>
    </Link>
  );
};
export default MovieComponent;
