import React, { useState } from "react";
import styled from "styled-components";
import MovieBuyTicketComponent from "./MovieBuyTicket/MovieBuyTicketComponent";
import MovieBuyTicketInfoComponent from "./MovieBuyTicket/MovieBuyTicketInfoComponent";
import { Link } from "react-router-dom";
import { Col, Button } from "antd";
import "antd/dist/antd.css";

const Container = styled.div`
  display: flex;
  flex-direction: column;
`;
const MovieListContainer = styled.div`
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  padding: 30px;
  gap: 25px;
  justify-content: space-evenly;;
`;
const Placeholder = styled.img`
  width: 120px;
  height: 120px;
  margin: 150px;
  opacity: 50%;
`;

function App() {
  const [movieList] = useState([
    {
        "Title": "Venom: Let There Be Carnage",
        "Year": "2021",
        "imdbID": "tt7097896",
        "Type": "movie",
        "Poster": "https://m.media-amazon.com/images/M/MV5BNzA4MjEzMTktZWMxYS00YWQ1LWJhMWItZTUyMGFmZjFmMjE4XkEyXkFqcGdeQXVyMDM2NDM2MQ@@._V1_SX300.jpg",
        "Href": "https://www.cinemaximum.com.tr/venom-zehirli-o%CC%88fke-2-filmi"
    },
    {
        "Title": "Cinni Kabus",
        "Year": "2021",
        "imdbID": "tt15466052",
        "Type": "movie",
        "Poster": "https://m.media-amazon.com/images/M/MV5BNjJiYzkyZTgtMjdkNy00ZGZiLWFlNTctMTI1Njg3YTIzYTdkXkEyXkFqcGdeQXVyMTI1NDEyNTM5._V1_SX300.jpg",
        "Href": "https://www.cinemaximum.com.tr/cinni-kabus-filmi"
    },
    {
        "Title": "Dayi: Bir Adamin Hikayesi",
        "Year": "2021",
        "imdbID": "tt11267076",
        "Type": "movie",
        "Poster": "https://m.media-amazon.com/images/M/MV5BYTlhMmQ4ZjAtNzEyYy00YTE4LWJjNzEtOThmOTZiNTgzOWJlXkEyXkFqcGdeQXVyMTI1NDAzMzM0._V1_SX300.jpg",
        "Href": "https://www.cinemaximum.com.tr/dayi-bir-adamin-hikayesi-filmi"
    },
    {
        "Title": "The Courier",
        "Year": "2020",
        "imdbID": "tt8368512",
        "Type": "movie",
        "Poster": "https://m.media-amazon.com/images/M/MV5BYjg4YTJlNzMtY2U0OC00N2FjLWI0ZTgtNGM1NDRhMWNhZmRkXkEyXkFqcGdeQXVyMDM2NDM2MQ@@._V1_SX300.jpg",
        "Href": "https://www.cinemaximum.com.tr/kurye-filmi"
    },
    {
      "Title": "Don't Breathe 2",
      "Year": "2021",
      "imdbID": "tt6246322",
      "Type": "movie",
      "Poster": "https://m.media-amazon.com/images/M/MV5BNWRmNzQ0N2QtZmJlYS00MWQ4LTlhY2ItODNmY2JkNGYyZTRlXkEyXkFqcGdeQXVyMDA4NzMyOA@@._V1_SX300.jpg",
      "Href": "https://www.cinemaximum.com.tr/nefesini-tut-2-filmi"
    },
    {
      "Title": "The War with Grandpa",
      "Year": "2020",
      "imdbID": "tt4532038",
      "Type": "movie",
      "Poster": "https://m.media-amazon.com/images/M/MV5BNTlkZDQ1ODEtY2ZiMS00OGNhLWJlZDctYzY0NTFmNmQ2NDAzXkEyXkFqcGdeQXVyMTkxNjUyNQ@@._V1_SX300.jpg",
      "Href": "https://www.cinemaximum.com.tr/iyi-olan-kazansin-filmi"
    },
    {
      "Title": "The Father",
      "Year": "2020",
      "imdbID": "tt10272386",
      "Type": "movie",
      "Poster": "https://m.media-amazon.com/images/M/MV5BZGJhNWRiOWQtMjI4OS00ZjcxLTgwMTAtMzQ2ODkxY2JkOTVlXkEyXkFqcGdeQXVyMTkxNjUyNQ@@._V1_SX300.jpg",
      "Href": "https://www.cinemaximum.com.tr/baba-filmi"
    },
    {
      "Title": "Avci Ilk Kehanet",
      "Year": "2021",
      "imdbID": "tt13051122",
      "Type": "movie",
      "Poster": "https://m.media-amazon.com/images/M/MV5BZTc2YzllMzMtYjMyZi00MThkLTg0NTgtOTY0ZTA0ZGNlMjQ1XkEyXkFqcGdeQXVyMTI1NDEyNTM5._V1_SX300.jpg",
      "Href": "https://www.cinemaximum.com.tr/avci-ilk-kehanet-filmi"
    },
  ]);
  const [selectedMovie, onMovieSelect] = useState();
  return (
    <Container>
      <Col style={{display: 'flex', justifyContent: 'center', alignItems: 'center', margin: '2rem'}}>
        <Link to='/movie-house'>
            <Button style={{cursor: 'pointer', width: '200px', marginRight: '2rem' }}>VÄ°ZYONDA</Button>
        </Link>
        <Link to='/coming-soon'>
          <Button style={{cursor: 'pointer', width: '200px' }}>YAKINDA</Button>
        </Link>
      </Col>
      {selectedMovie && <MovieBuyTicketInfoComponent selectedMovie={selectedMovie} onMovieSelect={onMovieSelect}/>}
      <MovieListContainer>
        {movieList?.length ? (
          movieList.map((movie, index) => (
            <MovieBuyTicketComponent
              key={index}
              movie={movie}
              onMovieSelect={onMovieSelect}
            />
          ))
        ) : (
          <Placeholder src=""/>
        )}
      </MovieListContainer>
    </Container>
  );
}

export default App;
