import React, { useState, useEffect } from "react";
import axios from "axios";
import { 
  Layout, 
  Input, 
  Row, 
  Col, 
  Card, 
  Tag, 
  Spin, 
  Alert, 
  Modal, 
  Typography 
} from 'antd';
import 'antd/dist/antd.css';

const gitHubUrl = "http://www.omdbapi.com/?t=comedy&apikey=a80b9720";

function App() {
  const [userData, setUserData] = useState({});

  useEffect(() => {
    getGiHubUserWithAxios();
  }, []);

  const getGiHubUserWithAxios = async () => {
    const response = await axios.get(gitHubUrl);
    setUserData(response.data);
  };

  useEffect(() => {
    getGitHubUserWithFetch();
  }, []);

  const getGitHubUserWithFetch = async () => {
    const response = await fetch(gitHubUrl);
    const jsonData = await response.json();
    setUserData(jsonData);
  };

  return (
    <Col style={{margin: '20px 0'}} className="gutter-row" span={4}>
      <div className="gutter-box">
      <Card
          style={{ width: 200 }}
          cover={
            <img
              alt={userData.Title}
              src={userData.Poster === 'N/A' ? 'https://placehold.it/198x264&text=Image+Not+Found' : userData.Poster}
          />
      }
      />
        <Tag className="info-item">{userData.Title}</Tag>
        <Tag className="info-item">{userData.Year}</Tag>
        <Tag className="info-item">{userData.Type}</Tag>

      </div>

      <div className="gutter-box">
      <Card
          style={{ width: 200 }}
          cover={
            <img
              alt={userData.Title}
              src={userData.Poster === 'N/A' ? 'https://placehold.it/198x264&text=Image+Not+Found' : userData.Poster}
          />
      }
      />
        <Tag className="info-item">{userData.Title}</Tag>
        <Tag className="info-item">{userData.Year}</Tag>
        <Tag className="info-item">{userData.Type}</Tag>

      </div>
    </Col>
  );
}

export default App;


