  return (
    <div className="container-fluid movie-app">
      <div className="row" style={{marginTop: '3rem', display: 'flex', flexDirection: 'row', justifyContent: 'space-evenly', alignItems: 'center', flexWrap: 'wrap'}}><MovieList movies={movies} /></div>
    </div>
  );
};

export default App;
