import React, { useState } from "react";
import styled from "styled-components";
import NewsComponent from "./NewsInfo/NewsComponent";
import NewsInfoComponent from "./NewsInfo/NewsInfoComponent";

const Container = styled.div`
  display: flex;
  flex-direction: column;
`;
const MovieListContainer = styled.div`
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  padding: 30px;
  gap: 55px;
  justify-content: space-evenly;;
`;
const Placeholder = styled.img`
  width: 120px;
  height: 120px;
  margin: 150px;
  opacity: 50%;
`;

function App() {
  const [movieList] = useState([
    {
      "Title": "IATSE Members Overwhelmingly Approve Strike Authorization; AMPTP Says It “Remains Committed To Reaching An Agreement",
      "Year": "04 October 2021",
      "Link": "https://deadline.com/2021/10/hollywood-strike-iatse-members-overwhelmingly-approve-authorization-1234843898/",
      "Type": "by David Robb and Dominic Patten",
      "Poster": "https://m.media-amazon.com/images/M/MV5BNDcwNTMzYmQtNzc4OS00NjA0LWFiNjMtNWZiMGY5N2Q1ZWFjXkEyXkFqcGdeQXVyMTE0MzQwMjgz._V1_SY300_CR166,0,200,300_AL_.jpg"
    },
    {
      "Title": "‘Venom 2’ Sinks Its Teeth Into $90 Million Domestic Debut, Shattering Pandemic-Era Record; Bond Soars Overseas While Sopranos Gets Whacked At Home",
      "Year": "03 October 2021",
      "imdbID": "tt0108778",
      "Type": "by Chris Nashawaty",
      "Poster": "https://m.media-amazon.com/images/M/MV5BYTc3ZTAwYTgtMmM4ZS00MDRiLWI2Y2EtYmRiZmE0YjkzMGY1XkEyXkFqcGdeQXVyMDA4NzMyOA@@._V1_SY300_CR1,0,200,300_AL_.jpg"
    },
    {
      "Title": "BAFTA Sets 2022 TV Awards Dates, Unveils Rule Changes",
      "Year": "04 October 2021",
      "imdbID": "tt2592910",
      "Type": "The Hollywood Reporter - Movie News",
      "Poster": "https://m.media-amazon.com/images/M/MV5BZDYxOTNkOGYtOGJlMC00MmMzLWI0YTYtOGQ2NzE1NjI2NTczXkEyXkFqcGdeQXVyMTE0MzQwMjgz._V1_SY300_CR166,0,200,300_AL_.jpg"
    },
    {
      "Title": "‘No Time to Die’ Ignites International Box Office With $119 Million Debut",
      "Year": "03 October 2021",
      "imdbID": "tt2467372",
      "Type": "by Rebecca Rubin",
      "Poster": "https://m.media-amazon.com/images/M/MV5BYWQ2NzQ1NjktMzNkNS00MGY1LTgwMmMtYTllYTI5YzNmMmE0XkEyXkFqcGdeQXVyMjM4NTM5NDY@._V1_SY300_CR1,0,200,300_AL_.jpg"
    },
    {
      "Title": "Recep Ivedik 3",
      "Year": "2010",
      "imdbID": "tt1577061",
      "Type": "movie",
      "Poster": "https://m.media-amazon.com/images/M/MV5BZjEwOTNlZjYtNTVlMS00M2Q5LTk0YmQtMWIzZDI4ODIyODlkXkEyXkFqcGdeQXVyNTM3NzExMDQ@._V1_SX300.jpg"
    },
    {
      "Title": "Archer",
      "Year": "2009–",
      "imdbID": "tt1486217",
      "Type": "series",
      "Poster": "https://m.media-amazon.com/images/M/MV5BMTg3NTMwMzY2OF5BMl5BanBnXkFtZTgwMDcxMjQ0NDE@._V1_SX300.jpg"
    },
    {
      "Title": "Once Upon a Time... in Hollywood",
      "Year": "2019",
      "imdbID": "tt7131622",
      "Type": "movie",
      "Poster": "https://m.media-amazon.com/images/M/MV5BOTg4ZTNkZmUtMzNlZi00YmFjLTk1MmUtNWQwNTM0YjcyNTNkXkEyXkFqcGdeQXVyNjg2NjQwMDQ@._V1_SX300.jpg"
    },
    {
      "Title": "Ghostbusters: Afterlife",
      "Year": "2021",
      "imdbID": "tt4513678",
      "Type": "movie",
      "Poster": "https://m.media-amazon.com/images/M/MV5BNDg3OWVlZGEtYjY3Ni00MzQ1LTgxOGYtYmVkMTY2MWIyMjEyXkEyXkFqcGdeQXVyMDA4NzMyOA@@._V1_SX300.jpg"
    },
  ]);
  const [selectedMovie, onMovieSelect] = useState();
  return (
    <Container>
      {selectedMovie && <NewsInfoComponent selectedMovie={selectedMovie} onMovieSelect={onMovieSelect}/>}
      <MovieListContainer>
        {movieList?.length ? (
          movieList.map((movie, index) => (
            <NewsComponent
              key={index}
              movie={movie}
              onMovieSelect={onMovieSelect}
            />
          ))
        ) : (
          <Placeholder src=""/>
        )}
      </MovieListContainer>
    </Container>
  );
}

export default App;
